const cloudinary=require("cloudinary").v2,fs=require("fs");cloudinary.config({cloud_name:process.env.CLOUDINARY_CLOUD_NAME,api_key:process.env.CLOUDINARY_API_KEY,api_secret:process.env.CLOUDINARY_API_SECRET});const uploadOnCloudinary=async(filePath,folder)=>{try{if(console.log("comes here",filePath),!filePath)return null;const response=await cloudinary.uploader.upload(filePath,{resource_type:"auto",folder:folder});return response}catch(error){return console.log("cloudinary upload error",error),null}};module.exports={uploadOnCloudinary:uploadOnCloudinary};